/** * Definition for singly-linked list. * public class ListNode { *     int val; *     ListNode next; *     ListNode() {} *     ListNode(int val) { this.val = val; } *     ListNode(int val, ListNode next) { this.val = val; this.next = next; } * } */ class Solution { public ListNode middleNode(ListNode head) { ListNode curr = head; int mid = 0; int cnt = 0; while(curr!=null){ cnt++; curr = curr.next; } curr = head; if(cnt%2==0){ mid =  (cnt/2)+1; }else{ mid = (cnt+1)/2; } cnt =1; while(cnt!=mid){ cnt++; curr = curr.next; } return curr; } }